import Values from 'values.js';
import isColor from 'is-color';

const Shades = (props) => {

    const [shades,setShades] = use

    useEffect(() => {
        if(isColor(props.color)){
            createShades();
        }
    },[props.color])

    const createShades = () =>{
        const shades_Color = new Values(props.color).shades(1);
        // console.log(shades_Color);
    }

    return (
        <div className="shades">
            <h1>Shades</h1>
        </div>
    )
}

